var link=document.querySelector(".feedback-button");var popup=document.querySelector(".modal-feedback");var overlay=document.querySelector(".overlay");var close=popup.querySelector(".modal-close");var form=popup.querySelector("form");var name=popup.querySelector("[name=name]");var email=popup.querySelector("[name=email]");var isStorageSupport=true;var storage="";try{storage=localStorage.getItem("name")}catch(err){isStorageSupport=false}link.addEventListener("click",function(a){a.preventDefault();overlay.classList.add("overlay-show");popup.classList.add("modal-show");if(storage){name.value=storage;email.focus()}else{name.focus()}});close.addEventListener("click",function(a){a.preventDefault();popup.classList.remove("modal-show");overlay.classList.remove("overlay-show");popup.classList.remove("modal-error")});form.addEventListener("submit",function(a){if(!name.value||!email.value){a.preventDefault();popup.classList.remove("modal-error");popup.offsetWidth=popup.offsetWidth;popup.classList.add("modal-error")}else{if(isStorageSupport){localStorage.setItem("name",name.value)}}});window.addEventListener("keydown",function(a){if(a.keyCode===27){a.preventDefault();if(popup.classList.contains("modal-show")){popup.classList.remove("modal-show");overlay.classList.remove("overlay-show");popup.classList.remove("modal-error")}}});